public class StringLowerCase24 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str ="A REAL-WORLD STACK allows operations AT ONE end ONLY.";
		
		String lower = str.toLowerCase();
		
		System.out.println("Original String: "+str);
		System.out.println("Lower-Cased String: "+lower);

	}

}
